package com.sdp.self_rpc_done.apolloJoinUp;import org.springframework.beans.factory.annotation.Value;/*************************************************** * Copyright (C) didi_global                       * FileName:         Registe_service                       * Author:           Sun Dongpo                    * Date:             2020/5/4 3:41 PM               * Description:                      * Tele:             15029155474                   * mail:             sundongpo_i@didiglobal.com    ************************************************** */public class Registe_service {    @Value("${service_regist_host}")    private String host;    @Value("${service_regist_port}")    private int port;    @Value("${service_regist_name}")    private String serviceName;    public String getHost() {        return host;    }    public int getPort() {        return port;    }    public String getServiceName() {        return serviceName;    }    @Override    public String toString() {        return "Registe_service{" +                "host='" + host + '\'' +                ", port=" + port +                ", serviceName='" + serviceName + '\'' +                '}';    }}