package com.sdp.self_rpc_done.publicInterface;import com.sdp.self_rpc_done.publicInterface.IMessageHandler;import com.sdp.self_rpc_done.publicInterface.MessageInput;import com.sdp.self_rpc_done.publicInterface.MessageOutput;import com.sdp.self_rpc_done.publicInterface.SelfResponse;import io.netty.channel.ChannelHandlerContext;// 找不到类型的消息统一使用默认处理器处理public class DefaultHandler implements IMessageHandler<MessageInput> {    @Override    public void handle(ChannelHandlerContext ctx, String requesetId, MessageInput input) {        System.out.println("unrecognized message type=" + input.getType() + " comes");        ctx.writeAndFlush(new MessageOutput("001","error",new SelfResponse(500L)));    }}