package com.sdp.self_rpc_done.publicInterface;// 指数RPC的输入public class SelfRequest {    private int base;    private int exp;    public SelfRequest() {    }    public SelfRequest(int base) {        this.base = base;    }    public SelfRequest(int base, int exp) {        this.base = base;        this.exp = exp;    }    public int getBase() {        return base;    }    public void setBase(int base) {        this.base = base;    }    public int getExp() {        return exp;    }    public void setExp(int exp) {        this.exp = exp;    }}